import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const slides = [
  {
    title: "Emergency response, simplified.",
    subtitle:
      "Report emergencies in seconds and track status updates until the case is resolved.",
    tag: "ğŸš¨ Faster response workflow",
    image:
      "data:image/jpeg;base64,"
  },{
    title: "Ambulance support when every second matters.",
    subtitle:
      "Create an ambulance request, share location details, and reduce response delay.",
    tag: "ğŸš‘ Ambulance assistance",
    image:
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQpiCSxZ02wpy8KBtZr1q4k9Bq-F3h6Kg1A3A&s",
  },
  {
    title: "Police support with clear tracking.",
    subtitle:
      "Report urgent police cases and follow the status from Pending to Resolved.",
    tag: "ğŸ‘® Police emergency workflow",
    image:
      "data:image/jpeg;base64,",
  },
  {
    title: "Find hospitals nearby instantly.",
    subtitle:
      "Use Maps to locate nearby hospitals and report medical emergencies quickly.",
    tag: "ğŸ¥ Nearest hospitals",
    image:
      "https://images.unsplash.com/photo-1586773860418-d37222d8fce3?auto=format&fit=crop&w=1400&q=80",
  },
];

export default function HeroCarousel() {
  const [active, setActive] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setActive((prev) => (prev + 1) % slides.length);
    }, 4200);

    return () => clearInterval(timer);
  }, []);

  const openMaps = () => {
    if (!navigator.geolocation) return alert("Geolocation not supported");

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        const lat = pos.coords.latitude;
        const lng = pos.coords.longitude;

        const url = `https://www.google.com/maps/search/hospitals/@${lat},${lng},14z`;
        window.open(url, "_blank");
      },
      () => alert("Please allow location permission to open maps.")
    );
  };

  return (
    <section className="hero">
      <div className="hero-card">
        {/* Background */}
        <div
          className="hero-bg"
          style={{ backgroundImage: `url(${slides[active].image})` }}
        />

        {/* Overlay */}
        <div className="hero-overlay" />

        {/* Content */}
        <div className="hero-content">
          <div className="hero-tag">{slides[active].tag}</div>

          <h1 className="hero-title">{slides[active].title}</h1>
          <p className="hero-sub">{slides[active].subtitle}</p>

          <div className="hero-buttons">
            <Link className="btn btn-red" to="/create-emergency">
              ğŸš¨ Create Emergency
            </Link>

            <button className="btn btn-blue" onClick={openMaps}>
              ğŸ—ºï¸ Find Hospitals Nearby
            </button>

            <Link className="btn btn-green" to="/register">
              âœ… Register Free
            </Link>
          </div>

          {/* Dots */}
          <div className="hero-dots">
            {slides.map((_, i) => (
              <button
                key={i}
                className={`dot ${active === i ? "active" : ""}`}
                onClick={() => setActive(i)}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}
